mapper.select:
SELECT *
FROM article
WHERE id = #{id}
  AND user_id = #{userId}

SELECT *
FROM article
WHERE user_id = #{userId}
ORDER BY created_at DESC;

SELECT *
FROM article
WHERE user_id = #{userId}
  AND category_id = #{categoryId}
ORDER BY created_at DESC;

mapper.update:
UPDATE article
SET category_id =

    #{categoryId}


    NULL

WHERE id IN

    #{id}
