mapper.insert:
INSERT INTO user (id, username, password, email, avatar, status, role, created_at, updated_at)
VALUES (#{user.id}, #{user.username}, #{user.password}, #{user.email}, #{user.avatar},
        #{user.status}, #{user.role}, #{user.createdAt}, #{user.updatedAt});

mapper.select:
SELECT *
FROM user
WHERE username = #{identifier}
  AND password = #{password}

SELECT username
FROM user
WHERE email = #{email}

mapper.update:
UPDATE user

    avatar   = #{avatar},
    username = #{username},
    password = #{password},
    updated_at = #{updatedAt}

WHERE id = #{id}

mapper.select:
select id
from user

    and username = #{username}
    and email = #{email}

select id
from user
where username = #{username}
